%                        EulerSample.m%% A sample Matlab script that implements the Euler's method for % the ODE %% dy/dt = F(t,y)%% where the function F(t,y) is specified using the anonymous function % specification. Note that in this sample, F(t,y), depends upon the % parameter lambda. F will use the value of lambda that precedes the% declaration of F. If one wants to use different values of lambda % in the function F within the same program, the F must be redeclared% after lambda is specified with the different values. %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%                    Target Function%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%F  = @(y)10*(y-y^3)%     			 %%tInitial    = 0.0;                      % Initial timetFinal      = 5.0;                      % Final timeyInitial    = 0.1;                      % Initial value of yN           = 51;                       % Number of timesteps takenh           = (tFinal - tInitial)/N;    % Timestep determinationy           = zeros(N+1,1);             % Arrays to hold solution valuest           = zeros(N+1,1); %%////////////////////////////////////////////////////////%     Computing the solution using Runge-Kutta method    %////////////////////////////////////////////////////////%t(1)  = tInitial;y(1)  = yInitial;     for(i = 1:N)        t(i+1) = t(i) + h;        y(i+1) = y(i) + .5*h*F(y(i)) +.5*h*F(y(i)+h*F(y(i)));  %Runge-Kuttaend  y(N+1)   %%    Set plot limits %tPlotMin =     tInitial;tPlotMax =     tFinal;yPlotMin =     0.0;yPlotMax =     2.0;% plot, then scale by calling axis command plot(t,y);axis([tPlotMin,tPlotMax,yPlotMin,yPlotMax]);title('Approximate solution to dy/dt = 10(y-y^3) obtained with Runge-Kutta method ')xlabel('t') ylabel('y(t)')